        this.query.push('AND');
        return this;
    }
    /**
     * Chains on an OR clause to the expression.
     * @type {Expression}
     */
    get or() {
        this.query.push('OR');
        return this;
    }
    /**
     * Chains on a `+` operator to the expression.
     * @type {Expression}
     */
    get plus() {
        this.query.push('+');
        return this;
    }
    /**
     * Chains on a `*` operator to the expression.
     * @type {Expression}
     */
    get times() {
        this.query.push('*');
        return this;
    }
    /**
     * Chains on a `-` operator to the expression.
     * @type {Expression}
     */
    get minus() {
        this.query.push('-');
        return this;
    }
    /**
     * Chains on a `/` operator to the expression.
     * @type {Expression}
     */
    get div() {
        this.query.push('/');
        return this;
    }
    /**
     * Chains on a `=` conditional to the expression.
     * @type {Expression}
     */
    get equals() {
        this.query.push('=');
        return this;
    }
    /**
     * Chains on a `=~` conditional to the expression to match regexes.
     * @type {Expression}
     */
