        this.re = new RegExp('[' + patterns + ']', 'g');
    }
    /**
     * Escape replaces occurrences of special characters within the target
     * string with the necessary escape codes.
     */
    escape(val) {
        if (val instanceof Raw) {
            return val.getValue();
        }
