        let escapedVal = '';
        let match = this.re.exec(val);
        while (match) {
            escapedVal += val.slice(chunkIndex, match.index) + this.escaper + match[0];
            chunkIndex = this.re.lastIndex;
            match = this.re.exec(val);
        }
        if (chunkIndex === 0) {
            return this.wrap + val + this.wrap;
        }
