    quoted: bindEsc(new Escaper(['"'], '"')),
    /**
     * stringLitEscaper escapes single quotes in string literals.
     */
    stringLit: bindEsc(new Escaper(['\''], '\'')),
    /**
     * tagEscaper escapes tag keys, tag values, and field keys.
