                .equals
                .value(42)
                .or
                .field('f')
                .equals
                .tag('my_"tag')
                .or
                .field('f')
                .equals
                .value(new Date(1475985480231))
                .or
                .field('f')
                .equals
                .value(toNanoDate('1475985480231035600'))
                .or
                .field('f')
                .equals
                .value(true)
                .or
                .exp(e => e.field('a').equals.value(1).or.field('b').equals.value(2))
                .or
