import { ExponentialBackoff } from '../../src/backoff/exponential';
import { Pool, RequestError, ServiceNotAvailableError } from '../../src/pool';
import { expect } from 'chai';
import * as http from 'http';
import * as sinon from 'sinon';
const hosts = 2;
describe('pool', () => {
    let pool;
    let clock;
    let server;
    let sid; // random string to avoid conflicts with other running tests
    const createPool = () => {
        return new Pool({
            backoff: new ExponentialBackoff({
                initial: 300,
                random: 0,
                max: 10 * 1000,
            }),
        });
    };
    beforeEach(done => {
        pool = createPool();
        sid = `${Date.now()}${Math.random()}`; // tslint:disable-line
        if (!process.env.WEBPACK) {
            const handler = require('../fixture/pool-middleware');
            server = http.createServer(handler());
            server.listen(0, () => {
                for (let i = 0; i < hosts; i += 1) {
                    pool.addHost(`http://127.0.0.1:${server.address().port}`);
                }
                done();
            });
        }
        else {
            for (let i = 0; i < hosts; i += 1) {
                pool.addHost(location.origin);
            }
            done();
        }
    });
    afterEach(done => {
        if (clock) {
            clock.restore();
        }
        if (!process.env.WEBPACK) {
            server.close(() => done());
        }
        else {
            done();
        }
    });
    it('attempts to make an https request', () => {
        const p = createPool();
        p.addHost('https://httpbin.org/get');
        return p.json({ method: 'GET', path: '/get' });
    });
    describe('request generators', () => {
        it('makes a text request', () => {
            return pool.text({ method: 'GET', path: '/pool/json' })
                .then(data => expect(data).to.equal('{"ok":true}'));
        });
        it('includes request query strings and bodies', () => {
            return pool.json({
                method: 'POST',
                path: '/pool/echo',
                query: { a: 42 },
                body: 'asdf',
            }).then(data => {
                expect(data).to.deep.equal({
                    query: 'a=42',
                    body: 'asdf',
                    method: 'POST',
                });
            });
        });
        it('discards responses', () => {
            return pool.discard({ method: 'GET', path: '/pool/204' });
        });
        it('parses JSON responses', () => {
            return pool.json({ method: 'GET', path: '/pool/json' })
                .then(data => expect(data).to.deep.equal({ ok: true }));
        });
        it('errors if JSON parsing fails', () => {
            return pool.json({ method: 'GET', path: '/pool/badjson' })
                .then(() => { throw new Error('Expected to have thrown'); })
                .catch(err => expect(err).to.be.an.instanceof(SyntaxError));
        });
    });
    it('times out requests', () => {
        pool.timeout = 1;
        return pool.text({ method: 'GET', path: '/pool/json' })
            .then(() => { throw new Error('Expected to have thrown'); })
            .catch(err => expect(err).be.an.instanceof(ServiceNotAvailableError))
            .then(() => pool.timeout = 10000);
    });
    it('retries on a request error', () => {
        return pool.text({ method: 'GET', path: `/pool/altFail-${sid}/json` })
            .then(body => expect(body).to.equal('{"ok":true}'));
    });
    it('fails if too many errors happen', () => {
        expect(pool.hostIsAvailable()).to.be.true;
        return pool.discard({ method: 'GET', path: '/pool/502' })
            .then(() => { throw new Error('Expected to have thrown'); })
            .catch(err => {
            expect(err).to.be.an.instanceof(ServiceNotAvailableError);
            expect(pool.hostIsAvailable()).to.be.false;
        });
    });
    it('calls back immediately on un-retryable error', () => {
        return pool.discard({ method: 'GET', path: '/pool/400' })
            .then(() => { throw new Error('Expected to have thrown'); })
            .catch(err => {
            expect(err).to.be.an.instanceof(RequestError);
            expect(err.res.statusCode).to.equal(400);
            expect(pool.hostIsAvailable()).to.be.true;
        });
    });
    it('pings servers', () => {
        return pool.ping(1000, `/pool/altFail-${sid}/ping`).then(results => {
            if (results[0].online) {
                [results[0], results[1]] = [results[1], results[0]];
            }
            expect(results[0].online).to.be.false;
            expect(results[1].online).to.be.true;
            expect(results[1].version).to.equal('v1.0.0');
        });
    });
    it('times out in pings', () => {
        return pool.ping(1).then(results => {
            expect(results[0].online).to.be.false;
            expect(results[1].online).to.be.false;
        });
    });
    describe('backoff', () => {
        beforeEach(() => {
            clock = sinon.useFakeTimers();
            return pool.discard({ method: 'GET', path: '/pool/502' })
                .catch(() => { });
        });
        it('should error if there are no available hosts', () => {
            return pool.discard({ method: 'GET', path: '/pool/json' })
                .then(() => { throw new Error('Expected to have thrown'); })
                .catch(err => {
                expect(err).to.be.an.instanceof(ServiceNotAvailableError);
                expect(err.message).to.equal('No host available');
            });
        });
        it('should reenable hosts after the backoff expires', () => {
            expect(pool.hostIsAvailable()).to.be.false;
            clock.tick(300);
            expect(pool.hostIsAvailable()).to.be.true;
        });
        it('should back off if failures continue', () => {
            clock.tick(300);
            expect(pool.hostIsAvailable()).to.be.true;
            return pool.discard({ method: 'GET', path: '/pool/502' })
                .then(() => { throw new Error('Expected to have thrown'); })
                .catch(err => {
                expect(err).to.be.an.instanceof(ServiceNotAvailableError);
                expect(pool.hostIsAvailable()).to.be.false;
                clock.tick(300);
                expect(pool.hostIsAvailable()).to.be.false;
                clock.tick(300);
                expect(pool.hostIsAvailable()).to.be.true;
            });
        });
        it('should reset backoff after success', () => {
            clock.tick(300);
            expect(pool.hostIsAvailable()).to.be.true;
            return pool.discard({ method: 'GET', path: '/pool/204' }).then(() => {
                return pool.discard({ method: 'GET', path: '/pool/502' });
            })
                .then(() => { throw new Error('Expected to have thrown'); })
                .catch(err => {
                expect(err).not.to.be.undefined;
                expect(pool.hostIsAvailable()).to.be.false;
                clock.tick(300);
                expect(pool.hostIsAvailable()).to.be.true;
            });
        });
    });
});

import * as results from '../../src/results';
import { expect } from 'chai';
describe('results', () => {
    it('parses a empty result', () => {
        expect(results.parse({
            results: [{
                    series: [{
                            name: 'test_series',
                            columns: [
                                'time',
                                'mean',
                            ],
                            values: [],
                        }],
                }],
        }).slice()).to.deep.equal([]);
    });
    it('parses a simple table of results', () => {
        const r = results.parseSingle({
            results: [{
                    series: [{
                            name: 'test_series',
                            columns: [
                                'time',
                                'mean',
                            ],
                            values: [
                                ['2016-09-25T16:12:51.787Z', 42],
                                ['2016-09-25T16:34:31.999Z', 44],
                            ],
                        }],
                }],
        });
        expect(r.slice()).to.deep.equal([
            { time: new Date(1474819971787), mean: 42 },
            { time: new Date(1474821271999), mean: 44 },
        ]);
        expect(r.groups()).to.deep.equal([
            { name: 'test_series', tags: {}, rows: r.slice() },
        ]);
        expect(r.group({ tag: 'a' })).to.deep.equal([]);
    });
    it('parses alternate epochs', () => {
        const r1 = results.parseSingle({
            results: [{
                    series: [{
                            name: 'test_series',
                            columns: [
                                'time',
                                'mean',
                            ],
                            values: [
                                [1474819971787, 42],
                                [1474821271999, 44],
                            ],
                        }],
                }],
        }, 'ms');
        expect(r1.slice()).to.deep.equal([
            { time: new Date(1474819971787), mean: 42 },
            { time: new Date(1474821271999), mean: 44 },
        ]);
        const r2 = results.parseSingle({
            results: [{
                    series: [{
                            name: 'test_series',
                            columns: [
                                'time',
                                'mean',
                            ],
                            values: [
                                [1474819971787000, 42],
                                [1474821271999000, 44],
                            ],
                        }],
                }],
        }, 'u');
        expect(r2.slice()).to.deep.equal([
            { time: new Date(1474819971787), mean: 42 },
            { time: new Date(1474821271999), mean: 44 },
        ]);
    });
    it('parses grouped results', () => {
        const r = results.parseSingle({
            results: [{
                    series: [{
                            name: 'test_series',
                            tags: { tag: 'a' },
                            columns: [
                                'mean',
                            ],
                            values: [
                                [1],
                                [2],
                            ],
                        }, {
                            name: 'test_series',
                            tags: { tag: 'b' },
                            columns: [
                                'mean',
                            ],
                            values: [
                                [3],
                                [4],
                            ],
                        }],
                }],
        });
        expect(r.slice()).to.deep.equal([
            { tag: 'a', mean: 1 },
            { tag: 'a', mean: 2 },
            { tag: 'b', mean: 3 },
            { tag: 'b', mean: 4 },
        ]);
        expect(r.groups()).to.deep.equal([
            { name: 'test_series', tags: { tag: 'a' }, rows: [{ tag: 'a', mean: 1 }, { tag: 'a', mean: 2 }] },
            { name: 'test_series', tags: { tag: 'b' }, rows: [{ tag: 'b', mean: 3 }, { tag: 'b', mean: 4 }] },
        ]);
        expect(r.group({ tag: 'a' })).to.deep.equal([
            { tag: 'a', mean: 1 },
            { tag: 'a', mean: 2 },
        ]);
        expect(r.group({ tag: 'b' })).to.deep.equal([
            { tag: 'b', mean: 3 },
            { tag: 'b', mean: 4 },
        ]);
        expect(r.group({ tag: 'c' })).to.deep.equal([]);
    });
    it('parses empty series', () => {
        const r1 = results.parseSingle({
            results: [{}],
        });
        expect(r1.slice()).to.deep.equal([]);
    });
    it('parses empty values', () => {
        const r1 = results.parseSingle({
            results: [{
                    series: [
                        { columns: ['user', 'admin'] },
                    ],
                }],
        });
        expect(r1.slice()).to.deep.equal([]);
    });
    it('throws error on an errorful series', () => {
        expect(() => results.parseSingle({
            results: [
                { error: 'user already exists' },
            ],
        })).to.throw(/already exists/);
    });
});

import { FieldType } from '../../src/grammar';
import { coerceBadly, Schema } from '../../src/schema';
import { expect } from 'chai';
describe('schema', () => {
    let schema;
    beforeEach(() => {
        schema = new Schema({
            database: 'my_db',
            measurement: 'my_measure',
            tags: ['my_tag'],
            fields: {
                int: FieldType.INTEGER,
                float: FieldType.FLOAT,
                string: FieldType.STRING,
                bool: FieldType.BOOLEAN,
            },
        });
    });
    describe('coerceBadly', () => {
        it('apparently works', () => {
            expect(coerceBadly({
                b: 42,
                a: true,
                c: 'hello"world',
            })).to.deep.equal([
                ['a', 'true'],
                ['b', '42'],
                ['c', '"hello\\"world"'],
            ]);
        });
    });
    describe('basic schema', () => {
        it('coerces data correctly', () => {
            expect(schema.coerceFields({
                int: 42,
                float: 43,
                string: 'hello"world',
                bool: true,
            })).to.deep.equal([
                ['bool', 'T'],
                ['float', '43'],
                ['int', '42i'],
                ['string', '"hello\\"world"'],
            ]);
        });
        it('accepts partial data', () => {
            expect(schema.coerceFields({
                int: 42,
            })).to.deep.equal([
                ['int', '42i'],
            ]);
        });
        it('coerces numeric string data', () => {
            expect(schema.coerceFields({
                int: '42',
            })).to.deep.equal([
                ['int', '42i'],
            ]);
        });
        it('strips null and undefined values', () => {
            expect(schema.coerceFields({
                int: 42,
                float: undefined,
                bool: null,
            })).to.deep.equal([
                ['int', '42i'],
            ]);
        });
        it('throws if wrong data type provided (bool)', () => {
            expect(() => schema.coerceFields({ bool: 42 })).to.throw(/expected bool/i);
            expect(() => schema.coerceFields({ bool: 'asdf' })).to.throw(/expected bool/i);
        });
        it('throws if wrong data type provided (float)', () => {
            expect(() => schema.coerceFields({ float: true })).to.throw(/expected numeric/i);
            expect(() => schema.coerceFields({ float: 'asdf' })).to.throw(/expected numeric/i);
        });
        it('throws if wrong data type provided (int)', () => {
            expect(() => schema.coerceFields({ int: true })).to.throw(/expected numeric/i);
            expect(() => schema.coerceFields({ int: 'asdf' })).to.throw(/expected numeric/i);
        });
        it('allows valid tags', () => {
            expect(schema.checkTags({ my_tag: 'value' })).to.deep.equal(['my_tag']);
            expect(schema.checkTags({})).to.deep.equal([]);
        });
        it('throws if invalid tags are provided', () => {
            expect(() => schema.checkTags({ whatever: 'value' })).to.throw(/extraneous tags/i);
        });
        it('throws if invalid fields are provided', () => {
            expect(() => expect(schema.coerceFields({ x: 42 }))).to.throw(/extraneous fields/i);
        });
    });
});
